INSERT INTO `config_info` VALUES (502, 'blog-friendlinks-dev.yaml', 'DEFAULT_GROUP', 'spring:\r\n  datasource:\r\n    driverClassName: com.mysql.cj.jdbc.Driver\r\n    url: jdbc:mysql://127.0.0.1:3306/mugu_blog_friendlinks?useUnicode=true&characterEncoding=UTF-8&zeroDateTimeBehavior=convertToNull&useSSL=false&allowMultiQueries=true&serverTimezone=Asia/Shanghai\r\n    username: root\r\n    password: Nov2014\r\n  servlet:\r\n    multipart:\r\n      enabled: true\r\n      # 限制文件上传的大小,默认1MB\r\n      max-file-size: 20MB\r\n      ## 请求大小，默认10MB\r\n      max-request-size: 20MB\r\n  redis:\r\n    host: 127.0.0.1\r\n    password: live4dream\r\n    port: 6379\r\n    timeout: 15000\r\n    jedis:\r\n      pool:\r\n        max-active: 600\r\n        max-idle: 300\r\n        max-wait: 15000\r\n        min-idle: 10\r\n  swagger:\r\n    basePackage: com.mugu.blog.friendlinks.controller\r\n    author:\r\n      name: 不才陈某\r\n      email: 1655378771@qq.com\r\n      url: http://chenjiabing666.github.io\r\n    apiInfo:\r\n      title: 友链模块接口\r\n      description: 友链相关的接口\r\n      version: 1.0\r\n      termsOfServiceUrl: http://ip:${server.port}/${spring.application.name}\r\n      license: license\r\n      licenseUrl: licenseUrl\r\nfeign:\r\n  client:\r\n    config:\r\n      ## default 设置的全局超时时间，指定服务名称可以设置单个服务的超时时间\r\n      blog-file-server:\r\n        connectTimeout: 3000\r\n        readTimeout: 3000\r\n    httpclient:\r\n      # 开启 Http Client\r\n      enabled: true', 'e2c46acb8a688af276c270e077d9bb7c', '2022-03-08 09:45:35', '2022-03-08 10:14:47', NULL, '0:0:0:0:0:0:0:1', '', '729f3090-638e-4b45-bfde-b91312ffe3d0', 'null', 'null', 'null', 'yaml', 'null');
INSERT INTO `config_info` VALUES (503, 'blog-gateway-dev.yaml', 'DEFAULT_GROUP', 'spring:\r\n  main:\r\n    allow-bean-definition-overriding: true\r\n  cloud:\r\n    gateway:\r\n      ## 路由\r\n      routes:\r\n        ## id只要唯一即可，名称任意\r\n        - id: blog-user-boot\r\n          uri: lb://blog-user-boot\r\n          predicates:\r\n            ## Path Route Predicate Factory断言\r\n            - Path=/blog-user-boot/**\r\n        - id: blog-auth-server\r\n          uri: lb://blog-auth-server\r\n          predicates:\r\n            ## Path Route Predicate Factory断言\r\n            - Path=/blog-auth-server/**\r\n        - id: blog-article\r\n          uri: lb://blog-article\r\n          predicates:\r\n            ## Path Route Predicate Factory断言\r\n            - Path=/blog-article/**\r\n        - id: blog-comments\r\n          uri: lb://blog-comments\r\n          predicates:\r\n            ## Path Route Predicate Factory断言\r\n            - Path=/blog-comments/**\r\n        - id: blog-picture\r\n          uri: lb://blog-picture\r\n          predicates:\r\n            ## Path Route Predicate Factory断言\r\n            - Path=/blog-picture/**\r\n        - id: blog-friendlinks\r\n          uri: lb://blog-friendlinks\r\n          predicates:\r\n            ## Path Route Predicate Factory断言\r\n            - Path=/blog-friendlinks/**\r\n        - id: blog-file-server\r\n          uri: lb://blog-file-server\r\n          predicates:\r\n            ## Path Route Predicate Factory断言\r\n            - Path=/blog-file-server/**\r\n    ## 整合sentinel，配置sentinel控制台的地址\r\n    sentinel:\r\n      transport:\r\n        ## 指定控制台的地址\r\n        dashboard: 127.0.0.1:8856\r\n        port: 8719\r\n        ## 指定本服务的公网IP，否则心跳包监听不到\r\n        client-ip: 127.0.0.1\r\n      #配置限流之后，响应内容\r\n      scg:\r\n        fallback:\r\n          ## 两种模式，一种是response返回文字提示信息，\r\n          ## 一种是redirect，重定向跳转，需要同时配置redirect(跳转的uri)\r\n          mode: response\r\n          ## 响应的状态\r\n          response-status: 200\r\n          ## 响应内容\r\n          response-body: \'{\"code\": 3002,\"msg\": \"网络拥挤，请稍后再试...\",\"data\": null}\'\r\n      ## nacos持久化配置\r\n      datasource:\r\n        ## 配置流控规则，名字任意\r\n        ds-flow:\r\n          nacos:\r\n            ## nacos的地址\r\n            server-addr: ${spring.cloud.nacos.config.server-addr}\r\n            username: ${spring.cloud.nacos.config.username}\r\n            password: ${spring.cloud.nacos.config.password}\r\n            namespace: ${spring.cloud.nacos.config.namespace}\r\n            ## 配置ID\r\n            dataId: ${spring.application.name}-flow\r\n            ## 配置分组，默认是DEFAULT_GROUP\r\n            groupId: DEFAULT_GROUP\r\n            ## 配置存储的格式\r\n            data-type: json\r\n            ## rule-type设置对应得规则类型，总共七大类型，在com.alibaba.cloud.sentinel.datasource.RuleType这个枚举类中有体现\r\n            # gw-flow 是网关流控，与其他微服务规则不通，根据routeId和API分组进行流控\r\n            rule-type: gw-flow\r\n        ## 配置降级规则，名字任意\r\n        ds-degrade:\r\n          nacos:\r\n            ## nacos的地址\r\n            server-addr: ${spring.cloud.nacos.config.server-addr}\r\n            username: ${spring.cloud.nacos.config.username}\r\n            password: ${spring.cloud.nacos.config.password}\r\n            namespace: ${spring.cloud.nacos.config.namespace}\r\n            ## 配置ID\r\n            dataId: ${spring.application.name}-degrade\r\n            ## 配置分组，默认是DEFAULT_GROUP\r\n            groupId: DEFAULT_GROUP\r\n            ## 配置存储的格式\r\n            data-type: json\r\n            ## rule-type设置对应得规则类型，总共七大类型，在com.alibaba.cloud.sentinel.datasource.RuleType这个枚举类中有体现\r\n            rule-type: degrade\r\n  redis:\r\n    host: 127.0.0.1\r\n    password: live4dream\r\n    port: 6379\r\n    timeout: 15000\r\n    jedis:\r\n      pool:\r\n        max-active: 600\r\n        max-idle: 300\r\n        max-wait: 15000\r\n        min-idle: 10\r\noauth:\r\n  # JWT令牌的key\r\n  server:\r\n    signKey: blog\r\n    ignore:\r\n      urls:\r\n        - /blog-auth-server/oauth/token\r\n        - /blog-auth-server/oauth/authorize\r\n        - /blog-auth-server/oauth/check_token\r\n        - /swagger-ui.html\r\n        - /swagger-ui/*\r\n        - /swagger-resources/**\r\n        - /v2/api-docs\r\n        - /v3/api-docs\r\n        - /webjars/**\r\n        - /doc.html\r\n        - /**/v2/api-docs\r\n        - /blog-article/front/**\r\n        - /blog-picture/front/**\r\n        - /blog-friendlinks/front/**\r\n        - /blog-comments/comment/list\r\n        - /blog-comments/comment/add\r\n        - /blog-comments/message/add\r\n        - /blog-comments/message/list\r\n        - /actuator/**\r\n        - /instances/**\r\n        \r\n', 'baa668743620ff1befc8cdec37f8ffb9', '2022-03-08 09:45:35', '2022-03-08 10:09:56', NULL, '0:0:0:0:0:0:0:1', '', '729f3090-638e-4b45-bfde-b91312ffe3d0', 'null', 'null', 'null', 'yaml', 'null');
