INSERT INTO `his_config_info` VALUES (0, 663, 'blog-article-dev.yaml', 'DEFAULT_GROUP', '', 'spring:\r\n  rabbitmq:\r\n    virtual-host: /\r\n    username: admin\r\n    password: novabeyond\r\n    host: 127.0.0.1\r\n    port: 5672\r\n    # 开启消息confirm模式，消息发送到Exchange则触发回调\r\n    publisher-confirm-type: correlated\r\n    ##消息的return回调，消息未送到匹配的队列时则触发回调，如果为false则直接丢弃\r\n    publisher-returns: true\r\n    ## 手动ack\r\n    listener:\r\n      direct:\r\n        acknowledge-mode: manual\r\n      simple:\r\n        acknowledge-mode: manual\r\n  elasticsearch:\r\n    rest:\r\n      uris: http://127.0.0.1:8851\r\n  datasource:\r\n    driverClassName: com.mysql.cj.jdbc.Driver\r\n    url: jdbc:mysql://127.0.0.1:3306/mugu_blog_article?useUnicode=true&characterEncoding=UTF-8&zeroDateTimeBehavior=convertToNull&useSSL=false&allowMultiQueries=true&serverTimezone=Asia/Shanghai\r\n    username: root\r\n    password: Nov2014\r\n  # swagger相关的配置\r\n  swagger:\r\n    basePackage: com.mugu.blog.article.controller\r\n    author:\r\n      name: 不才陈某\r\n      email: 1655378771@qq.com\r\n      url: http://chenjiabing666.github.io\r\n    apiInfo:\r\n      title: 文章模块\r\n      description: 文章相关的接口\r\n      version: 1.0\r\n      termsOfServiceUrl: http://ip:${server.port}/${spring.application.name}\r\n      license: license\r\n      licenseUrl: licenseUrl\r\n  redis:\r\n    host: 127.0.0.1\r\n    password: \r\n    port: 6379\r\n    timeout: 15000\r\n    jedis:\r\n      pool:\r\n        max-active: 600\r\n        max-idle: 300\r\n        max-wait: 15000\r\n        min-idle: 10\r\nfeign:\r\n  sentinel:\r\n    enabled: true\r\n  client:\r\n    config:\r\n      ## default 设置的全局超时时间，指定服务名称可以设置单个服务的超时时间\r\n      default:\r\n        connectTimeout: 1000\r\n        readTimeout: 1000\r\n    httpclient:\r\n      # 开启 Http Client\r\n      enabled: true\r\n  ## 开启压缩\r\n  compression:\r\n    request:\r\n      enabled: true\r\n      ## 开启压缩的阈值，单位字节，默认2048，即是2k，这里为了演示效果设置成10字节\r\n      min-request-size: 1024\r\n      mime-types: text/xml,application/xml,application/json\r\n    response:\r\n      enabled: true', '40d0b1096ffac6b8f56f9774389eee66', '2022-03-08 09:45:35', '2022-03-08 09:45:35', NULL, '0:0:0:0:0:0:0:1', 'I', '729f3090-638e-4b45-bfde-b91312ffe3d0');
INSERT INTO `his_config_info` VALUES (0, 664, 'blog-auth-server-dev.yaml', 'DEFAULT_GROUP', '', 'spring:\r\n  datasource:\r\n    driverClassName: com.mysql.cj.jdbc.Driver\r\n    url: jdbc:mysql://127.0.0.1:3306/mugu_blog_auth_server?useUnicode=true&characterEncoding=UTF-8&zeroDateTimeBehavior=convertToNull&useSSL=false&allowMultiQueries=true&serverTimezone=Asia/Shanghai\r\n    username: root\r\n    password: Nov2014\r\n  # swagger相关的配置\r\n  swagger:\r\n    basePackage: com.mugu.blog.oauth.server.controller\r\n    author:\r\n      name: 不才陈某\r\n      email: 1655378771@qq.com\r\n      url: http://chenjiabing666.github.io\r\n    apiInfo:\r\n      title: OAuth server 模块\r\n      description: OAuth相关的接口\r\n      version: 1.0\r\n      termsOfServiceUrl: http://ip:${server.port}/${spring.application.name}\r\n      license: license\r\n      licenseUrl: licenseUrl\r\n  redis:\r\n    host: 127.0.0.1\r\n    password: \r\n    port: 6379\r\n    timeout: 15000\r\n    jedis:\r\n      pool:\r\n        max-active: 600\r\n        max-idle: 300\r\n        max-wait: 15000\r\n        min-idle: 10\r\noauth:\r\n  server:\r\n    signKey: blog\r\nfeign:\r\n  ## 开启压缩\r\n  compression:\r\n    request:\r\n      enabled: true\r\n      ## 开启压缩的阈值，单位字节，默认2048，即是2k，这里为了演示效果设置成10字节\r\n      min-request-size: 1024\r\n      mime-types: text/xml,application/xml,application/json\r\n    response:\r\n      enabled: true\r\n  client:\r\n    config:\r\n      ## default 设置的全局超时时间，指定服务名称可以设置单个服务的超时时间\r\n      default:\r\n        connectTimeout: 2000\r\n        readTimeout: 2000\r\n  httpclient:\r\n    enabled: true', 'e1c9ed849fccc956524cc9c3ae6cb70b', '2022-03-08 09:45:35', '2022-03-08 09:45:35', NULL, '0:0:0:0:0:0:0:1', 'I', '729f3090-638e-4b45-bfde-b91312ffe3d0');
