INSERT INTO `his_config_info` VALUES (0, 756, 'share-nacos-registry.yaml', 'DEFAULT_GROUP', '', 'spring:\r\n  cloud:\r\n    nacos:\r\n      ## 注册中心配置\r\n      discovery:\r\n        # nacos的服务地址，nacos-server中IP地址:端口号\r\n        server-addr: ${spring.cloud.nacos.config.server-addr}\r\n        username: ${spring.cloud.nacos.config.username}\r\n        password: ${spring.cloud.nacos.config.password}\r\n        namespace: ${spring.cloud.nacos.config.namespace}\r\n        metadata:\r\n          management:\r\n            ## 修改spring boot admin获取actuator信息的默认地址\r\n            context-path: ${server.servlet.context-path}/actuator', '65a13d8aa8b36b6318440bf40462dcdb', '2022-03-08 09:45:35', '2022-03-08 09:45:35', NULL, '0:0:0:0:0:0:0:1', 'I', '729f3090-638e-4b45-bfde-b91312ffe3d0');
