INSERT INTO `his_config_info` VALUES (0, 759, 'share-seata.yaml', 'DEFAULT_GROUP', '', '## 客户端seata的相关配置\r\nseata:\r\n  ## 是否开启seata，默认true\r\n  enabled: true\r\n  application-id: ${spring.application.name}\r\n  ## seata事务组的名称，一定要和config.tx(nacos)中配置的相同\r\n  tx-service-group: ${seata.application-id}-tx-group\r\n  ## 配置中心的配置\r\n  config:\r\n    ## 使用类型nacos\r\n    type: nacos\r\n    ## nacos作为配置中心的相关配置，需要和server在同一个注册中心下\r\n    nacos:\r\n      ## 命名空间，需要server端(registry和config)、nacos配置client端(registry和config)保持一致\r\n      namespace: ${spring.cloud.nacos.config.namespace}\r\n      ## 地址\r\n      server-addr: ${spring.cloud.nacos.discovery.server-addr}\r\n      ## 组， 需要server端(registry和config)、nacos配置client端(registry和config)保持一致\r\n      group: SEATA_GROUP\r\n      ## 用户名和密码\r\n      username: ${spring.cloud.nacos.config.username}\r\n      password: ${spring.cloud.nacos.config.password}\r\n  registry:\r\n    type: nacos\r\n    nacos:\r\n      ## 这里的名字一定要和seata服务端中的名称相同，默认是seata-server\r\n      application: seata-server\r\n      ## 需要server端(registry和config)、nacos配置client端(registry和config)保持一致\r\n      group: ${seata.config.nacos.group}\r\n      namespace: ${seata.config.nacos.namespace}\r\n      username: ${seata.config.nacos.username}\r\n      password: ${seata.config.nacos.password}\r\n      server-addr: ${spring.cloud.nacos.discovery.server-addr}', '03e245231153c1b51333a8d3399fa4e5', '2022-03-08 09:45:35', '2022-03-08 09:45:35', NULL, '0:0:0:0:0:0:0:1', 'I', '729f3090-638e-4b45-bfde-b91312ffe3d0');
